<?php

namespace KD2;

require __DIR__ . '/_assert.php';
require KD2FW_ROOT . '/FileInfo.php';

test(FileInfo::guessMimeType(file_get_contents(__FILE__)) == 'text/x-php', 'text/x-php guess error');

$type = 'text/xml';
$content = '<?xml version="1.0"?><content>OK</content>';
test(FileInfo::guessMimeType($content) == $type, $type . ' guess error');

$type = 'text/html';
$content = '<html><body>LOL</body></html>';
test(FileInfo::guessMimeType($content) == $type, $type . ' guess error');

$type = 'application/octet-stream';
test(FileInfo::guessMimeType(file_get_contents('/bin/cat')) == $type, $type . ' guess error');

$type = 'image/gif';
$content = 'R0lGODlhAQABAID/AMDAwAAAACH5BAEAAAAALAAAAAABAAEAAAEBMgA7';
test(FileInfo::guessMimeType(base64_decode($content)) == $type, $type . ' guess error');

$type = 'image/png';
$content = 'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABmJLR0QAAAAAAAD5Q7t/AAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH1woJFRUczQ4K3AAAAA1JREFUCNdj+P///2YACa8DseGFVHQAAAAASUVORK5CYII=';
test(FileInfo::guessMimeType(base64_decode($content)) == $type, $type . ' guess error');

$type = 'application/x-gzip';
$content = 'H4sICBo9VVMAA3Rlc3QudHh0APP35gIAP2zfdwMAAAA=';
test(FileInfo::guessMimeType(base64_decode($content)) == $type, $type . ' guess error');

$type = 'application/pdf';
$content = '';
test(FileInfo::guessMimeType(base64_decode($content)) == $type, $type . ' guess error');
